<span class="line"><span class="token keyword">const</span> Settings <span class="token operator">=</span> <span class="token function">lazy</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=></span> <span class="token keyword">import</span><span class="token punctuation">(</span><span class="token string">'./pages/Settings'</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
