<span class="line">    <span class="token keyword">const</span> visibleItemsCount <span class="token operator">=</span> Math<span class="token punctuation">.</span><span class="token function">ceil</span><span class="token punctuation">(</span>height <span class="token operator">/</span> itemHeight<span class="token punctuation">)</span> <span class="token operator">+</span> <span class="token number">2</span> <span class="token comment">// 额外渲染2行作为缓冲</span></span>
